import React from 'react';

const plans = [
    {
      id: 1,
      subject: '複習微積分',
      day: 0, // 星期 6（index 0）
      start: '13:30',
      end: '15:30',
    },
    {
      id: 2,
      subject: '複習初會',
      day: 3, // 星期 9（index 3）
      start: '13:00',
      end: '17:00',
    }
  ];

const PlanPage = () => {
  // 星期幾標題（6 到 12 號為例）
  const days = ['6', '7', '8', '9', '10', '11', '12'];
  const times = Array.from({ length: 12 }, (_, i) => 12 + i); // 12 到 23 點（12 格）

  return (
    <div style={{ padding: '2rem' }}>
      <h2>讀書計畫</h2>

      {/* 上方日期 */}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '1rem' }}>
        <button>{'<'}</button>
        <h3 style={{ margin: '0 1rem' }}>10 月 6 日 - 10 月 12 日</h3>
        <button>{'>'}</button>
      </div>

      {/* 表格本體 */}
      <div style={{ display: 'grid', gridTemplateColumns: '80px repeat(7, 1fr)', border: '1px solid #ccc' }}>
        {/* 星期標題列 */}
        <div></div>
        {days.map((day, idx) => (
          <div key={idx} style={{ textAlign: 'center', padding: '0.5rem', backgroundColor: '#f2f2f2' }}>
            {day}
          </div>
        ))}

        {/* 每個半小時一列 */}
        {times.map((hour) => (
          <>
            {/* 時間軸（左欄） */}
            <div
              key={hour}
              style={{
                borderTop: '1px solid #ccc',
                padding: '0.5rem',
                fontSize: '0.85rem',
                backgroundColor: '#fff',
              }}
            >
              {`${hour}:00`}
            </div>

            {/* 每一小時的七格 */}
            {days.map((_, idx) => {
  const cellTime = `${hour}:00`;

  // 檢查是否有對應的計畫
  const planInThisCell = plans.find(plan => {
    const startHour = parseInt(plan.start.split(':')[0]);
    const endHour = parseInt(plan.end.split(':')[0]);
    return plan.day === idx && hour >= startHour && hour < endHour;
  });

  return (
    <div
      key={`${hour}-${idx}`}
      style={{
        borderTop: '1px solid #ccc',
        borderLeft: idx === 0 ? '1px solid #ccc' : '',
        height: '40px',
        backgroundColor: idx % 2 === 0 ? '#fff' : '#f9f9f9',
        position: 'relative',
      }}
    >
        {planInThisCell && (
            <div
                style={{
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                height: '100%',
                backgroundColor: 'rgba(255, 147, 41, 0.7)',
                color: '#000',
                fontSize: '12px',
                padding: '2px',
                borderRadius: '4px',
            }}
            >
        <strong>{planInThisCell.subject}</strong>
        <div>{planInThisCell.start} - {planInThisCell.end}</div>
            </div>
        )}
    </div>
  );
})}

          </>
        ))}
      </div>
    </div>
  );
};

export default PlanPage;
