import React from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, CartesianGrid, ResponsiveContainer } from 'recharts';

const data = [
  { date: '12/1', Java: 4, Python: 2, C: 1, Calculus: 3 },
  { date: '12/2', Java: 3, Python: 2, C: 1, Calculus: 2 },
  { date: '12/3', Java: 4, Python: 1, C: 2, Calculus: 3 },
  { date: '12/4', Java: 2, Python: 1, C: 1, Calculus: 1 },
  { date: '12/5', Java: 3, Python: 2, C: 2, Calculus: 4 },
];

const TrackerPage = () => {
  return (
    <div style={{ padding: '2rem' }}>
      <h2>成效追蹤</h2>
      <h3>綜合學習時間</h3>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="Java" stackId="a" fill="#4472c4" />
          <Bar dataKey="Python" stackId="a" fill="#ed7d31" />
          <Bar dataKey="C" stackId="a" fill="#70ad47" />
          <Bar dataKey="Calculus" stackId="a" fill="#5b9bd5" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default TrackerPage;
