import React from 'react';

const plans = [
  {
    id: 1,
    subject: '複習微積分',
    day: 0,
    start: '13:30',
    end: '15:30',
  },
  {
    id: 2,
    subject: '複習初會',
    day: 3,
    start: '13:00',
    end: '17:00',
  },
];

const PlanPage = () => {
  const days = ['6', '7', '8', '9', '10', '11', '12'];
  const times = Array.from({ length: 12 }, (_, i) => 12 + i);

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center' }}>讀書計畫</h2>

      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '1rem' }}>
        <button style={{ padding: '0.5rem 1rem' }}>{'<'}</button>
        <h3 style={{ margin: '0 1rem' }}>10 月 6 日 - 10 月 12 日</h3>
        <button style={{ padding: '0.5rem 1rem' }}>{'>'}</button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '80px repeat(7, 1fr)', border: '1px solid #ccc' }}>
        <div></div>
        {days.map((day, idx) => (
          <div key={`day-${idx}`} style={{ textAlign: 'center', padding: '0.5rem', backgroundColor: '#f2f2f2' }}>
            {day}
          </div>
        ))}

        {times.map((hour) => (
          <React.Fragment key={hour}>
            {/* 整點列 */}
            <div
              style={{
                gridRow: 'span 2',
                borderTop: '1px solid #ccc',
                padding: '0.5rem',
                fontSize: '0.85rem',
                backgroundColor: '#fff',
                height: '40px',
              }}
            >
              {`${hour}:00`}
            </div>

            {[0, 0.5].map((offset, halfIdx) => (
              <React.Fragment key={`${hour}-${offset}`}>
                {days.map((_, idx) => {
                  const currentTime = hour + offset;
                  const planInThisHalf = plans.find(plan => {
                    const start = parseInt(plan.start.split(':')[0]) + parseInt(plan.start.split(':')[1]) / 60;
                    const end = parseInt(plan.end.split(':')[0]) + parseInt(plan.end.split(':')[1]) / 60;
                    return plan.day === idx && currentTime >= start && currentTime < end;
                  });
                  return (
                    <div
                      key={`${hour}:${offset === 0 ? '00' : '30'}-${idx}`}
                      style={{
                        borderTop: offset === 0.5 ? '1px dashed #ccc' : '1px solid #ccc',
                        borderLeft: idx === 0 ? '1px solid #ccc' : '',
                        height: '20px',
                        backgroundColor: (idx % 2 === 0 ? '#fff' : '#f9f9f9'),
                        position: 'relative',
                      }}
                    >
                      {planInThisHalf && (
                        <div
                          style={{
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            backgroundColor: 'rgba(255, 147, 41, 0.7)',
                            color: '#000',
                            fontSize: '12px',
                            padding: '2px',
                            borderRadius: '4px',
                          }}
                        >
                          <strong>{planInThisHalf.subject}</strong>
                          <div>{planInThisHalf.start} - {planInThisHalf.end}</div>
                        </div>
                      )}
                    </div>
                  );
                })}
              </React.Fragment>
            ))}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
};

export default PlanPage;
