import React from 'react';

const PlanPage = () => {
  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'left' }}>讀書計畫</h2>

      {/* 日期範圍 */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        margin: '1rem 0'
      }}>
        <button>{'<'}</button>
        <h3>10 月 6 日 - 10 月 12 日</h3>
        <button>{'>'}</button>
      </div>

      {/* 時間表容器（下一步會補） */}
      <div style={{ border: '1px solid #ccc', height: '500px' }}>
        {/* 時間軸與每日排程表將放這裡 */}
      </div>
    </div>
  );
};

export default PlanPage;
